import React, { useState, useRef } from 'react';

function App() {
  const [recording, setRecording] = useState(false);
  const [transcript, setTranscript] = useState('');
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    mediaRecorderRef.current = new MediaRecorder(stream);
    audioChunksRef.current = [];

    mediaRecorderRef.current.ondataavailable = (e) => {
      audioChunksRef.current.push(e.data);
    };

    mediaRecorderRef.current.onstop = async () => {
      const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
      const formData = new FormData();
      formData.append('audio', audioBlob, 'voice.webm');

      try {
        const response = await fetch('http://localhost:5000/api/converse', {
          method: 'POST',
          body: formData,
        });

        // Blob response: audio file
        if (response.ok) {
          const audioBlob = await response.blob();
          const audioUrl = URL.createObjectURL(audioBlob);

          // Play AI voice response
          const audio = new Audio(audioUrl);
          audio.play();

          // Optional: Show text fallback (if needed)
          setTranscript('(AI responded via voice)');
        } else {
          const error = await response.json();
          setTranscript(error?.error || 'Something went wrong.');
        }
      } catch (err) {
        console.error(err);
        setTranscript('Failed to fetch AI response.');
      }
    };

    mediaRecorderRef.current.start();
    setRecording(true);
  };

  const stopRecording = () => {
    mediaRecorderRef.current.stop();
    setRecording(false);
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>AI Voice Food Assistant</h1>
      <button onClick={startRecording} disabled={recording}>üéôÔ∏è Start Recording</button>
      <button onClick={stopRecording} disabled={!recording}>üõë Stop</button>
      <p><strong>Transcript:</strong> {transcript}</p>
    </div>
  );
}

export default App;
